	public static void genCodeInstructions(){
		
	
		
		BufferedReader reader = null;
		MyFileWriter writer = null;
		String line;
		

	
		try {
			
			
			reader = new BufferedReader(new FileReader(POPULATE_FILE));
			writer = new MyFileWriter(OUTPUT);
			
			while((line= reader.readLine())!= null){
				
				if(line.startsWith(INSERT_INTO)){
						writer.writeIntoFile("\"" + line + "\", \n" );
				}
			}
			

		} catch (FileNotFoundException e) {
			throw new RuntimeException("Problem Finding instructions file : "+ e.getMessage());

		} catch (IOException e) {
			throw new RuntimeException("Problem while reading statements: "+ e.getMessage());

		}finally {
			try {
				if(reader != null)
					reader.close();
				if(writer != null)
					writer.closeWriter();
			} catch (IOException e) {
				throw new RuntimeException("Problem while closing file: "+ e.getMessage());

			}

		}

	}
